const { createClient } = require('@supabase/supabase-js');
const OpenAI = require('openai');
const pdf = require('pdf-parse');

// Configuración con tus credenciales
const SUPABASE_URL = 'process.env.SUPABASE_URL';
const SUPABASE_KEY = 'process.env.SUPABASE_KEY';
const OPENAI_API_KEY = 'process.env.OPENAI_API_KEY';

// Clientes
const supabase = createClient(SUPABASE_URL, SUPABASE_KEY);
const openai = new OpenAI({ apiKey: OPENAI_API_KEY });

// Obtener PDF del webhook
const binaryData = items[0].binary.data;
if (!binaryData) {
  throw new Error('No PDF received');
}

const pdfBuffer = Buffer.from(binaryData.data, 'base64');

// Extraer texto del PDF
const pdfData = await pdf(pdfBuffer);
const fullText = pdfData.text;

// Dividir en chunks
const chunkSize = 1000;
const chunkOverlap = 200;
const chunks = [];

for (let i = 0; i < fullText.length; i += chunkSize - chunkOverlap) {
  chunks.push(fullText.substring(i, i + chunkSize));
}

// Procesar cada chunk
const results = [];
for (const chunk of chunks) {
  // Generar embedding con OpenAI
  const embeddingResponse = await openai.embeddings.create({
    model: 'text-embedding-ada-002',
    input: chunk,
  });

  const embedding = embeddingResponse.data[0].embedding;

  // Insertar en Supabase
  const { data, error } = await supabase
    .from('manual_audaces')
    .insert({
      content: chunk,
      metadata: { filename: binaryData.fileName || 'manual.pdf' },
      embedding: embedding
    });

  if (error) {
    console.error('Supabase error:', error);
  } else {
    results.push({ success: true, chunk: chunk.substring(0, 50) + '...' });
  }
}

return {
  json: {
    success: true,
    chunksProcessed: results.length,
    totalChunks: chunks.length,
    message: `Successfully processed ${results.length} chunks from PDF`
  }
};
